class TileableNoise{constructor(r,fx,tx,fy,ty){if(this.fromX=fx,this.toX=tx,this.seed=Math.random(),this.simplexNoise=new SimplexNoise(this.seed),3===arguments.length)this.fromY=fx,this.toY=tx,this.r1=this.r2=r;else{if(5!==arguments.length)throw new Error("Um número inválido de parâmetros foi dado ("+arguments.length+"), ao invés de 3 ou 5");this.fromY=fy,this.toY=ty,this.r1=r,this.r2=r*Math.abs(ty-fy)/Math.abs(tx-fx)}}eval1D(x,t=0){var i=(x-this.fromX)/(this.toX-this.fromX)*2*Math.PI,h=this.r1*Math.cos(i),e=this.r1*Math.sin(i);return this.simplexNoise.noise3D(h,e,t)}eval2D(x,y){var i=(x-this.fromX)/(this.toX-this.fromX)*2*Math.PI,h=(y-this.fromY)/(this.toY-this.fromY)*2*Math.PI,e=this.r1*Math.cos(i),o=this.r1*Math.sin(i),r=this.r2*Math.cos(h),a=this.r2*Math.sin(h);return this.simplexNoise.noise4D(e,o,r,a)}newSeed(s=Math.random()){return this.seed=s,this.simplexNoise=new SimplexNoise(this.seed),this}}